tinymce.PluginManager.add('filemanager', function(editor) {
    function openmanager() {
        var win, data, dom = editor.dom,
            imgElm = editor.selection.getNode();
        var width, height, imageListCtrl;
        win = editor.windowManager.open({
            title: 'Файловый менеджер',
            file: tinyMCE.baseURL + '/plugins/filemanager/dialog.php?editor=' + editor.id + '&lang=' + tinymce.settings.language + '&subfolder=' + tinymce.settings.subfolder + '&dir=' + tinymce.settings.file_dir,
            filetype: 'all',
            classes: 'filemanager',
            width: 900,
            height: 600,
            inline: 1
        })
    }
    editor.addButton('filemanager', {
        icon: 'browse',
        tooltip: 'Вставить файл',
        onclick: openmanager,
        stateSelector: 'img:not([data-mce-object])'
    });
    editor.addMenuItem('filemanager', {
        icon: 'browse',
        text: 'Вставить файл',
        onclick: openmanager,
        context: 'insert',
        prependToContext: true
    })
});